import React from 'react';
import './style.scss';
import ProfileIcon from '../ProfileIcon';
import moment from 'moment';
import * as Icon from 'react-feather'
import { useSelector, useDispatch } from 'react-redux'

moment.relativeTimeThreshold('d', 30 * 12);
moment.updateLocale('en', {
    relativeTime: {
        dd: "%dd",
    }
});

const CommentList = ({ accountName, comment, commentImage, commentTime, userpost }) => {

    const userData = useSelector(({ user }) => user.currentUser)

    const isCommentOwned = userpost.comments.filter((comment) => {
        return comment._user == userData._id
    });

    return (
        <React.Fragment>
            <div className="commentContainer">
                {commentImage && (
                    <div className="profile-icon">
                        <img src={commentImage ? commentImage : require('../../assets/default-avatar.png')} alt="image" aria-label="poster-iamge" />
                    </div>
                )}
                <div className="accountName">{accountName}</div>
                <div className="comment">{comment}</div>
                <div>
                    {isCommentOwned && <Icon.Trash2 size={15} style={{ justifySelf: "flex-end" }} />}

                </div>
            </div>

            <h3 className="comment-time"> {moment(commentTime).fromNow(true)}</h3>

        </React.Fragment>

    )
};

export default CommentList;
