        <div className="wrapper">

            <NavLink to={'/settings/edit/'}> <span>Edit Profile</span></NavLink>


            <Switch>
                <Route exact path="/settings/edit/" component={EditProFileForm} />
                <Route exact path="/settings/password" render={() => <ChangePasswordForm />} />
            </Switch>
        </div>
    )
}

export default EditProfilePage;
