const ExploreScreen = ({ getPosts }) => {

    useEffect(() => {
        let subscirbe = true;
        if (subscirbe) {
            getPosts()
        }
        return () => subscirbe = null
    }, []);

    return (

        <div>

            <p> welcome to the explore page</p>

        </div>
    )
};


export default connect(null, { getPosts })(ExploreScreen);
