import React, { Fragment, } from 'react';
import { connect } from 'react-redux'
import './style.scss';
import * as Icon from 'react-feather';

//linebreak component

const LineBreak = () => {
    return (
        <div style={{
            border: "1px solid lightgrey",
            height: "100%",
            width: "2px",
            opacity: .7
        }}>
        </div>
    )
}

const SideMenu = ({ user, isLoading, currentUser }) => {
    // console.log(user)

    const [defaultSelected, setdefaultSelected] = React.useState(true)

    return (
        <div className="wrapper">
            { !currentUser && <h1> Loading..</h1>}
            {
                currentUser && (
                    <Fragment>

                        <section className="header">
                            <h2> Photogram</h2>
                        </section>
                        <section className="user-avatar">
                            <img src={currentUser && currentUser.avatar} alt="" />
                        </section>

                        <div className="user-info">
                            <h4 className="name">{currentUser.name}</h4>
                            <p className="username"> @{currentUser.username}</p>
                        </div>

                        <div className="post_info">

                            <div className="posts">
                                <h4> 40</h4>
                                <p> Posts</p>
                            </div>
                            <LineBreak />
                            <div className="followers">
                                <h4> 200</h4>
                                <p> Followers</p>
                            </div>
                            <LineBreak />
                            <div className="following">
                                <h4> 200</h4>
                                <p> Following</p>
                            </div>
                        </div>

                        <div className="menu-list">
                            <ul>
                                <li>
                                    <Icon.Menu color={defaultSelected ? "tomato" : "black"} />
                                    <h4> Feeds</h4>
                                </li>
                                <li>
                                    <Icon.Search />
                                    <h4>Explore</h4>
                                </li>
                                <li>
                                    <Icon.LogOut />
                                    <h4> Log out </h4>
                                </li>

                            </ul>
                        </div>

                    </Fragment>
                )
            }
        </div>
    )
};
const mapStateToProps = ({ user }) => ({
    currentUser: user.currentUser,
    isLoding: user.isLoading
});
export default connect(mapStateToProps)(SideMenu);
