import feedTypes from './feedTypes';

const initState = {
    posts: NULL,
    fetching: true,
    error: false,
    hasMore: false,
};

const feedReducer = (state = initState, { type, payload }) => {

    switch (type) {
        case feedTypes.FETCH_POSTS_SUCCESS:
            return {
                ...state,
                error: false,
                fetching: false,
                posts: [...state.posts, payload]
            }

        case feedTypes.FETCH_POSTS_FAILURE:
            return {
                ...state,
                fetching: false,
                error: payload
            }

        default:
            return state;
    }
}

export default feedReducer;
