import React, { useState, useEffect, Fragment } from 'react'
import './style.scss';

import axios from 'axios';
import { setToken } from '../../utils';
import Loader from '../../component/Loader';
import cogoToast from 'cogo-toast'
import { useParams } from 'react-router-dom'
import { useSelector } from 'react-redux';
import Avatar from '../../assets/default-avatar.png';
import * as Icon from 'react-feather';



const token = localStorage.getItem('authToken');

const ProfilePage = () => {
    const [profile, setProfile] = useState(undefined);
    const { username } = useParams();
    const currentUser = useSelector(({ user }) => user.currentUser)

    console.log(currentUser)
    useEffect(() => {


        const getProfile = async () => {
            if (token) {
                setToken(token)
            }
            const config = {
                headers: {
                    "Content-Type": "applocation/json"
                }
            }
            try {
                const response = await axios.get(`/api/route/user/userprofile/${username}`, config)
                if (response) {
                    const data = response.data.userProfile[0]
                    await setProfile(data)
                }
            } catch (error) {

                if (error) {
                    cogoToast.error(`${error.message}`)
                }
                console.log(error)
            }
        }

        getProfile()
        return () => null;
    }, [])



    if (!profile) {
        return <Loader />
    }

    return (
        <div className="profilepage__wrapper">


            <header className="header">
                <div className="header__content">
                    <div className="profile__image">
                        <img src={profile.avatar ? profile.avatar : Avatar} alt="" />
                    </div>
                    <div className="profile__details">

                        <div className="username">
                            <h2> {profile.username}</h2>
                            {profile._id !== currentUser._id ? <button> Follow</button>

                                :
                                (
                                    <Fragment>
                                        <button className="settings__btn">
                                            Edit Profile
                                            </button>
                                        <button >
                                            <Icon.Settings />
                                        </button>
                                    </Fragment>
                                )

                            }
                        </div>

                        <div className="follow__details">
                            <small>{profile.posts && profile.posts.length ? profile.posts.length : 0} posts</small>
                            <small>{profile.followers && profile.followers.length ? profile.followers.length : 0} followers</small>
                            <small> {profile.following && profile.following.length ? profile.following.length : 0} following</small>
                            <small></small>
                        </div>

                        <div className="name">
                            <p> {profile.name}</p>
                        </div>

                    </div>
                </div>
            </header>

            <main>
                <div className="post__title">

                </div>

            </main>





        </div>
    )
}

export default ProfilePage
