import React, { useState } from 'react'
import './style.scss';
import { ReactComponent as CardButton } from '../../../assets/cardButton.svg'
import CardMenu from '../CardMenu';
import CommentList from '../../CommentList';
import Profile from '../../Profile';
import moment from 'moment';
import ExploreCardMenu from '../../ExplorePostCardMenu'

function Card(props) {

    const [commentText, setCommentText] = useState('');

    const { storyBorder,
        image,
        comments,
        likedByText,
        likedByNumber,
        accountName,
        avatar,
        hours,
        feed, } = props;

    const handlePost = () => {

    }
    return (
        <div className="card">
            <header>
                <Profile iconSize="medium" image={avatar} username={accountName} storyBorder={storyBorder} />
                <CardButton className="cardButton" />
            </header>
            <img className="cardImage" src={image} alt="card content" />
            <CardMenu />
            <div className="likedBy">
                {/*  <Profile iconSize="small" hideAccountName={true} /> */}
                {/*  <span>
                    Liked by <strong>{likedByText}</strong> and{" "}
                    <strong>{likedByNumber} others</strong>
                </span> */}
            </div>
            <div className="comments">
                {comments && comments.map((comment) => {
                    return (
                        <CommentList
                            key={comment._id}
                            accountName={comment.user}
                            comment={comment}
                            commentId={comment._id}
                            commentuser={comment._user}
                            commentText={comment.commentText}
                            userpost={feed}
                            accountName={comment.username}
                            comment={comment.commentText}
                            commentImage={comment.avatar}
                            commentTime={comment.commentDate}

                        />
                    );
                })}
            </div>
            <div className="timePosted">{hours} HOURS AGO</div>
            <div className="addComment">
                <input value={commentText} onChange={(e) => setCommentText(e.target.value)} type="text" placeholder="Add a comment..." className="commentText" />
                <button disabled={commentText ? false : true} className="postText-btn" onSubmit={handlePost}>Post</button>
            </div>
        </div>
    )
}

export default Card
