import React from 'react';
import './style.scss';
import Card from './Card';
// import { useHistory } from 'react-router-dom';

import { useSelector } from 'react-redux';
import { v4 as uuidv4 } from 'uuid';

const Cards = () => {

    const feeds = useSelector(({ post }) => post.feeds);

    const commentsOne = [
        {
            user: "raffagrassetti",
            text: "Woah dude, this is awesome! ðŸ”¥",
            id: 1,
        },
        {
            user: "therealadamsavage",
            text: "Like!",
            id: 2,
        },
        {
            user: "mapvault",
            text: "Niceeeee!",
            id: 3,
        },
        {
            user: "mapvault",
            text: "Niceeeee!",
            id: 4,
        },

        {
            user: "mapvault",
            text: "Niceeeee!",
            id: 5,
        },
        {
            user: "mapvault",
            text: "Niceeeee!",
            id: 6,
        },

    ];

    const commentsTwo = [
        {
            user: "mapvault",
            text: "Amazing content, keep it up!",
            id: 4,
        },
    ];

    const commentsThree = [
        {
            user: "dadatlacak",
            text: "Love this!",
            id: 5,
        },
    ];

    if (!feeds || feeds.length < 1) {
        return <p> Loading...</p>
    }
    return (
        <div className="cards">
            {/* stories components for later */}
            { feeds && feeds.map((feed) => {

                return (
                    <Card
                        avatar={feed.author.avatar}
                        key={uuidv4()}
                        accountName={feed.author.username}
                        storyBorder={true}
                        image={feed.postMedia}
                        comments={feed.postComments && feed.postComments.comments ? feed.postComments.comments : null}
                        likedByText={feed.postLikes && feed.postLikes.likes ? feed.postLikes.likes.username : null}
                        // likedByNumber={feed.postLikes && feed.postLikes.likes ? feed.postLikes.likes.length : "Be the first to Like this"}
                        hours={16}
                    />
                )

            })}
            {/*    <Card
                accountName="mapvault"
                image="https://picsum.photos/800"
                comments={commentsTwo}
                likedByText="therealadamsavage"
                likedByNumber={47}
                hours={12}
            /> */}

        </div>
    )
}

export default Cards;
