import React, { useEffect, useState } from 'react'

import './style.scss';
import { Switch, Route, NavLink, useRouteMatch, Link, } from 'react-router-dom'
import { useSelector } from 'react-redux';
import Avatar from '../../assets/default-avatar.png'
import * as Icon from 'react-feather'
import ChangePasswordForm from '../../component/changePasswordForm';
import EditProFileForm from '../../component/EditProfileForm';

function EditProfilePage() {
    // useEffect(() => {

    // }, [window.outerWidth]);

    const user = useSelector(({ user }) => user.currentUser)
    const [name, setName] = useState('')
    const [username, setUsername] = useState('')
    const [website, setWebsite] = useState('')
    const [bio, setBio] = useState('')

    const [showDefault, setShowDefault] = useState(true)

    if (window.outerWidth < 640) {
        document.querySelector('.navigation').style.display = "none"
    }

    const handleFormSubmit = (e) => {

        e.preventDefaut();
    }
    let { path, url } = useRouteMatch();
    return (
        <div className="Edit_wrapper">

            <div>
                <div>
                    <ul>
                        <li>
                            <Link to={path}> Edit Profile</Link>
                        </li>

                        <li>
                            <Link to={`${path}/changePassword`}> Change password</Link>

                        </li>
                    </ul>
                </div>

                <div>
                    <Switch>
                        <Route exact path={path} >
                            <EditProFileForm />
                        </Route>
                        <Route exact path={`${path}/changePassword`}>

                            <ChangePasswordForm />
                        </Route>
                    </Switch>

                </div>


            </div>


        </div>
    )
}

export default EditProfilePage;
