import React, { useState, useEffect, Fragment } from 'react';
import './style.scss'
import Header from '../../component/Header';
import PostCard from '../../component/Explorepostcard/PostCard';

import { getPosts } from '../../redux/Actions/postActions'
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import * as Routes from '../../component/routes';

import MobileTabMenu from '../../component/MobileTabMenu';

const ExploreScreen = ({ getPosts, posts, location }) => {

    const history = useHistory();
    useEffect(() => {
        let subscirbe = true;
        if (subscirbe) {
            getPosts(history)
        }
        return () => subscirbe = null
    }, [getPosts]);

    console.log(history)







    return (

        <div className="container">
            <Header />
            <p> Explore </p>
            <div>
                <div className="posts-wrapper">
                    {posts && posts.length && posts.map((post) => {
                        const { _id, date, postedBy, likes, comments, postMedia } = post;
                        return (

                            <div className="post-container" key={_id}>
                                <PostCard
                                    post={post}
                                    id={_id}
                                    date={date}
                                    postedBy={postedBy}
                                    media={postMedia}
                                    likes={likes}
                                    comments={comments}
                                />
                            </div>


                        )
                    })}

                </div>
            </div>
            <div className="menuTab-Wrapper">

                <MobileTabMenu />
            </div>
        </div>
    )
};

const mapStateTOProps = ({ post }) => {
    return {
        posts: post.posts
    }
}
export default connect(mapStateTOProps, { getPosts })(ExploreScreen);
