const Wrapper = styled.div`
    width:  270px; 
    /* max-width: 100%; */
    height: 400px;

    & > *:nth-child(18n + 2) {
        grid-column: 2 / -1;
        grid-row: span 2;
      }

      & > *:nth-child(18n + 10) {
        grid-column: 1 / span 2;
        grid-row: span 2;
      }
    /* display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center; */

`
const PostMedia = styled.embed`
width: 100%;
height: 100%;
/* min-height: inherit;
max-height: inherit; */
object-fit: cover;
display: block;
/* border-radius: 15px; */
&:focus{
    outline: 0;
}

`
const BottomWrapper = styled.div`
min-width: 100%;
max-width: 300px;
display: flex;
justify-content: space-between;
align-items: center;
margin: 10px 0px;
/* height: 5vh; */

`
const PosterAvatar = styled.img`
width: 100%;
height: 100%;
object-fit: cover;
border-radius:50%;

`


