import React, { useState, useEffect, Fragment } from 'react'
import { useSelector } from 'react-redux';
import './style.scss';
import cogoToast from 'cogo-toast';

function EditProFileForm() {

    const user = useSelector(({ user }) => user.currentUser);
    const [name, setName] = useState('');
    const [username, setUsername] = useState('')
    const [bio, setBio] = useState('')
    const [email, setEmail] = useState('')


    useEffect(() => {
        document.title = 'Edit Profile • Photoshare';
    }, []);

    const handleFormSubmit = (e) => {
        e.preventDefaut();
        try {
            cogoToast.info('button has been clicked')
        } catch (error) {

        }
    }
    return (
        <div className="form_wrapper">
            <form action="" className="Edit_profile_form" onSubmit={handleFormSubmit}>
                <div className="name">
                    <label htmlFor="name">Name</label>
                    <input
                        id="name"
                        type="text"
                        name="name"
                        placeholder={'Name'}
                        value={user && user.name ? user.name : name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div className="username">
                    <label htmlFor="name">Username</label>
                    <input
                        id="username"
                        type="text"
                        name="username"
                        placeholder={'Username'}
                        value={user && user.username ? user.username : username}
                        onChange={(e) => setUsername(e.target.value)}
                    />

                </div>
                <div className="bio">
                    <label htmlFor="bio">Bio</label>
                    <input
                        id="bio"
                        name="bio"
                        placeholder={'Bio'}
                        value={user && user.bio ? user.bio : bio}
                        onChange={(e) => setBio(e.target.value)}
                    />


                </div>
                <div className="email">
                    <label htmlFor="name">Email</label>
                    <input
                        id="email"
                        type="text"
                        name="email"
                        placeholder={'Email'}
                        value={user && user.email ? user.email : email}
                        onChange={(e) => setEmail(e.target.value)}
                    />

                </div>

                <div className="btn__wrapper">

                    <button className="submit__btn" type="submit">Submit</button>
                </div>


            </form>
        </div>
    )
}

export default EditProFileForm
