import React, { Fragment, useEffect, useState } from 'react'
import Modal from 'react-modal';
import { useDispatch, useSelector } from 'react-redux';
import './style.scss';
import * as Routes from '../../routes'
import { showModal, hideModal } from '../../../redux/modal/modalActions';
import { useHistory } from 'react-router-dom'
import * as Icon from 'react-feather';

const NewPostModal = ({ file }) => {
    const modalIsOpen = useSelector(({ modal }) => modal.showModal)
    const dispatch = useDispatch()
    const history = useHistory();
    const [previewImage, setPreviewImage] = useState(undefined)
    const [caption, setCaption] = useState('')



    useEffect(() => {
        dispatch(showModal())
        return () => null;
    }, [])

    useEffect(() => {
        if (file.type == "image/jpeg" || file.type == "image/png") {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = (event) => {
                setPreviewImage(event.target.result);
            };
        } else {
            // 
        }
        return () => null
    }, [file])


    const closeModal = async () => {
        dispatch(hideModal())
        history.push(Routes.Dashboard)
    }
    return (
        <div className="modal__wrapper">
            <Modal
                isOpen={modalIsOpen}

                onRequestClose={closeModal}
                className="modal"
                overlayClassName="overlay"
            >
                <div className="modal__header">
                    <button className="goback__btn"><Icon.ArrowLeft /></button>
                    <button className="share__btn"> Share</button>

                </div>
                <small> Create Post</small>
                {/* caption */}
                <input
                    placeholder=" write a Caption"
                    className="caption__box"
                    name="caption"
                    id="caption"
                    type="text"
                    style={{ resize: "none" }}
                    value={caption}
                    onChange={(e) => setCaption(e.target.value)}
                />

                {/* preview image */}

                {previewImage && (<div className="preview__wrapper">
                    <img src={previewImage} className="preview" alt="image" />
                </div>)}
                <h4> hello</h4>
                <button onClick={closeModal}>close</button>
            </Modal>
        </div>
    )
};

export default NewPostModal
