import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom';
import './style.scss';

import { useFormik } from 'formik';
import * as Yup from 'yup';
import { BiHide, BiShowAlt } from 'react-icons/bi'
import { IconContext } from 'react-icons';
import axios from 'axios';


function SignUpForm() {

    const validationSchema = Yup.object().shape({
        fullName: Yup.string().required('fullname is required'),
        username: Yup.string().required('username is required'),
        email: Yup.string().email('please enter a valid email address').required(),
        password: Yup.string().required('required'),


    });

    const [showPassword, setShowPassword] = useState(false)
    const handleShowToggle = () => {

        setShowPassword(prev => !prev)
    }
    const handleFormSubmit = async (values) => {

        try {
            const data = new FormData();
            data.append('fullName', values.fullName);
            data.append('username', values.username);
            data.append("email", values.email);
            data.append('password', values.password);
            data.append('avatar', values.avatar);

            const res = await axios.post('http://localhost:9000/api/route/user/register', data);
            console.log(res.data)
        } catch (error) {
            console.log(error)
        }
        return console.log(values);

    };
    const formik = useFormik({
        initialValues: {
            fullName: '',
            username: "",
            email: "",
            password: '',
            avatar: null,
        },
        validationSchema,

        onSubmit: handleFormSubmit,
    });


    const { values, errors, handleChange, handleSubmit, setFieldValue } = formik;
    return (
        <Fragment>

            <form encType="multipart/form-data" onSubmit={handleSubmit} >
                <h2>Sign up</h2>
                <div className="name-input">
                    <input
                        name="fullName"
                        type="text"
                        placeholder="Full Name"
                        value={values.fullName}
                        onChange={handleChange}
                    />
                </div>
                {errors.fullName && <small className="error" >{errors.fullName}</small>}

                <div className="username-input">

                    <input
                        type="text"
                        name="username"
                        placeholder="username"
                        value={values.username}
                        onChange={handleChange}
                    />
                </div>
                {errors.username && <small className="error" >{errors.username}</small>}
                <div className="email-input">

                    <input
                        type="email"
                        name="email"
                        placeholder="email"
                        value={values.email}
                        onChange={handleChange}
                    />
                </div>
                {errors.email && <small className="error">{errors.email}</small>}
                <div className="password-input">
                    <input
                        type={showPassword ? "text" : "password"}
                        name="password"
                        placeholder="password"
                        value={values.password}
                        onChange={handleChange}
                    />
                    {showPassword ?
                        <button className="btn-icon" type="button" onClick={handleShowToggle}> <BiShowAlt />
                        </button> :
                        <button type="button" className="btn-icon" onClick={handleShowToggle}>
                            <BiHide />
                        </button>
                    }

                </div>
                {errors.password && <small className="error">{errors.password}</small>}
                <div className="file-input">
                    <input
                        type="file"
                        name="avatar"
                        onChange={(event) => setFieldValue("avatar", event.target.files[0])}
                    />

                </div>
                {errors.file && <small className="error">{errors.file}</small>}
                <button type="submit" className="signup-btn"> Sign Up</button>
                <p> already have an account? <Link to="/"> Log in</Link></p>
            </form>



        </Fragment>

    )
}

export default SignUpForm
