import React, { useState, Fragment, useEffect } from 'react'
import './style.scss';

import { useSelector, useDispatch } from 'react-redux';


const FollowButton = ({ userId }) => {

    const dispatch = useDispatch();
    const currentUser = useSelector(({ user }) => user.currentUser);

    const [following, setFollowed] = useState(undefined)

    const handleUnfollow = () => {
        setFollowed(false)
    }
    const handleFollow = () => {
        setFollowed(true)
    }

    useEffect(() => {
        if (currentUser) {
            if (currentUser.following.find((follow) => String(follow._id) == String(userId))) {
                setFollowed(true)
            }

        }
    }, [])

    if (!currentUser) {
        return <p> loading...</p>
    }
    if (currentUser._id == userId) {
        return <button disabled>
            follow
        </button>
    }
    return (
        <Fragment>

            { following == true && <button> UnFollow</button>}
            {following == false && <button> Follow</button>}


        </Fragment>

    )
}

export default FollowButton;
