import React, { useEffect } from 'react'
import { Wrapper, ContentWrapper, MobileHeader } from './style';
import { useSelector } from 'react-redux';
import Avatar from '../../assets/default-avatar.png'
import * as Icon from 'react-feather'

function EditProfilePage() {
    useEffect(() => {
        if (window.outerWidth < 640) {
            document.querySelector('.navigation').style.display = "none"
        }
    }, [window.outerWidth]);

    const user = useSelector(({ user }) => user.currentUser)
    return (
        <Wrapper >


            <MobileHeader>

                <div>
                    <Icon.ArrowLeft />
                </div>

                <div className="title">Edit Profile</div>

            </MobileHeader>
            <ContentWrapper>

                <div className="profile_data">

                    <div className="profile_image">
                        <img src={user && user.avatar ? user.avatar : Avatar} alt="" />
                    </div>

                    <div className="name">
                        <blockquote> {user && user.username}</blockquote>
                        <label style={{ cursot: "pointer" }} htmlFor="#profile">
                            Change Profile Photo
                        </label>
                        <input id="profile" type="file" accept="image/*" style={{ display: "none" }} />
                    </div>

                </div>



            </ContentWrapper>
        </Wrapper>
    )
}

export default EditProfilePage;
