import React from 'react';
import styled from 'styled-components'


const Wrapper = styled.div`
    width:  270px;
    min-height: 200px;
    max-height: 400px;
`
const PostImage = styled.img`

width: 100%;
min-height: inherit;
max-height: inherit;
object-fit: cover;
border-radius: 15px;
`
const BottomWrapper = styled.div`
width: 100%;
border: 1px solid red;
height: 20px;

`



function PostCard({ key, date, media, likes, comments }) {
    return (
        <Wrapper>
            <PostImage alt="post image" src={media} />
            <BottomWrapper>

            </BottomWrapper>
        </Wrapper>
    )
}

export default PostCard
