import React from 'react';
import styled from 'styled-components'


const Wrapper = styled.div`
    width:  270px;
    min-height: 200px;
    max-height: 400px;
`
const PostImage = styled.img`

width: 100%;
min-height: inherit;
max-height: inherit;
object-fit: cover;
border-radius: 15px;
`
const BottomWrapper = styled.div`
width: 100%;
border: 1px solid red;
display: flex;
align-items: center;
/* height: 5vh; */

`
const PosterAvatar = styled.img`

width: 100%;
height: 100%;
object-fit: contain;
border-radius:50%;


`


const styles = {
    postAvatarContainer: {
        width: "50px",
        height: "50px",
        borderRadius: "50%",
        border: "1.3px solid tomato",
        padding: '10px',
    }
}
function PostCard({ key, date, media, likes, comments, postedBy }) {
    return (
        <Wrapper>
            <PostImage alt="post image" src={media} />
            <BottomWrapper>
                <div style={styles.postAvatarContainer}>
                    <PosterAvatar src={postedBy.avatar} alt="poster img" />

                </div>
            </BottomWrapper>
        </Wrapper>
    )
}

export default PostCard
