import React, { Fragment, useState, useRef } from 'react';
import './style.scss';
import axios from 'axios';
import { setToken } from '../../utils'
import * as Icon from 'react-feather';
import PopUpCard from '../UsersPopUpCard/PopUpCard';
import Loader from '../Loader';
import Avatar from '../../assets/default-avatar.png';
import { v4 as uuidv4 } from 'uuid';
import Divider from '../Divider'
const token = localStorage.getItem('authToken');

const SearchBox = ({ }) => {
  const [query, setQuery] = useState('');
  const [userDetails, setUserDetails] = useState([])
  const [isSearching, setIsSearching] = useState(undefined);
  const inputRef = useRef();


  const fetchUsers = async (query) => {
    if (token) {
      setToken(token)
    }
    try {
      await setIsSearching(true)
      await setQuery(query);
      const res = await axios.get(`/api/route/user/search-users/${query}`)
      if (res) {
        await setUserDetails([...userDetails, res.data.users]);
        await setIsSearching(false);
        console.log(userDetails);

      }


    } catch (error) {
      if (error.response) {
        console.log(error.response.data.msg)
        setIsSearching(false)

      }
    }
  }

  return (
    <Fragment>
      <form className="search-box" onSubmit={(event) => event.preventDefault()}>
        <input
          ref={inputRef}
          onChange={(e) => fetchUsers(e.target.value)}
          onClick={(onClick) => console.log('serach ready')}
          value={query}
          className="search-box__input"
          placeholder="Search"
        />
        <span className="search-box__placeholder">
          <Icon.Search className="searchbox__icon" size={16} />
          {/* fetching && <Loader /> */}
          {isSearching && <Loader />}
        </span>
      </form>
      {isSearching === false && (
        <PopUpCard >
          {userDetails.length > 0 && userDetails[0].map(({ avatar, name, username }) => {
            return (
              <Fragment key={uuidv4()}>
                <li className="search__list">
                  <img src={avatar ? avatar : Avatar} alt="" width={30} height={30} />
                  <div>
                    <h5>{username}</h5>
                    <h6>{name}</h6>
                  </div>
                </li>
                <Divider />
              </Fragment>
            )
          })}
        </PopUpCard>

      )}
    </Fragment>
  );
};

export default SearchBox;
