import React from 'react';
import Notifications from "../../../assets/notifications.svg";
import Inbox from "../../../assets/inbox.svg";
import Bookmark from "../../../assets/bookmark.svg";
import LikeButton from '../../Button/LikeButton';
import './style.scss';
import * as Icon from 'react-feather'

import { connect } from 'react-redux';


const CardMenu = ({ focus, likeFunc, isLiked, unlikeFunc, userpost, user }) => {

    const handleButtonClicked = () => {
        try {
            likeFunc()
            checkUserLiked()
        } catch (error) {
            console.log(error);
        }
    }
    const checkUserLiked = userpost.likes.find((like) => {
        return like.likedBy.toString() == user._id.toString();
    })
    return (
        <div className="cardMenu">
            <div className="interactions">

                <Icon.Heart
                    className="icon" fill={checkUserLiked ? 'tomato' : "transparent"} onClick={handleButtonClicked} />
                <Icon.ThumbsDown className="icon" onClick={unlikeFunc} />
                <Icon.MessageCircle className="icon" onClick={focus} />
                <Icon.Share className="icon" />
            </div>
            <Icon.Bookmark className="icon" />
        </div>
    )
};

const mapStateToProps = ({ user }) => {
    return {
        user: user.currentUser
    }
}
export default connect(mapStateToProps, null)(CardMenu);
