import modalTypes from './modalTypes';

const initState = {
    showModal: false,
}

const modalReducer = (state = initState, { type, payload }) => {

    switch (type) {
        case modalTypes.SHOW_MODAL:
            return {
                ...state,
                showModal: true
            }
        case modalTypes.HIDE_MODAL:
            return {
                ...state,
                showModal: false
            }
    }
}

export default modalReducer;
