import React, { useEffect, useState } from 'react'
import {
    Wrapper,
    ContentWrapper,
    MobileHeader,
    ProfileForm
} from './style';
import { useSelector } from 'react-redux';
import Avatar from '../../assets/default-avatar.png'
import * as Icon from 'react-feather'

function EditProfilePage() {
    // useEffect(() => {
    //     if (window.outerWidth < 640) {
    //         document.querySelector('.navigation').style.display = "none"
    //     }
    // }, [window.outerWidth]);

    const user = useSelector(({ user }) => user.currentUser)
    const [name, setName] = useState('')
    const [username, setUsername] = useState('')
    const [website, setWebsite] = useState('')
    const [bio, setBio] = useState('')

    const handleFormSubmit = (e) => {

        e.preventDefaut();
    }
    return (
        <Wrapper >


            {/* <MobileHeader>

                <div>
                    <Icon.ArrowLeft />
                </div>

                <div className="title">Edit Profile</div>

            </MobileHeader> */}
            <ContentWrapper>

                <div className="profile_data">

                    <div className="profile_image">
                        <img src={user && user.avatar ? user.avatar : Avatar} alt="" />
                    </div>

                    <div className="name">
                        <b> {user && user.username}</b>
                        <label style={{ cursot: "pointer", color: "blue" }} htmlFor="profile">
                            Change Profile Photo
                        </label>
                        <input id="profile" type="file" accept="image/*" style={{ display: "none", cursor: "pointer" }} />
                    </div>

                </div>

            </ContentWrapper>



            <ProfileForm onSubmit={handleFormSubmit}>
                <div className="name__input">
                    <label htmlFor="Name">Name</label>
                    <input
                        type="text"
                        name="name"
                        id="Name"
                        value={user && user.name ? user.name : name}
                        onChange={(e) => setName(e.target.value)}

                    />

                </div>

                <div className="username__input">
                    <label htmlFor="Username">Username</label>
                    <input
                        type="text"
                        name="username"
                        id="Username"
                        value={user && user.username ? user.username : username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div className="website__input">
                    <label htmlFor="Website">Website</label>
                    <input
                        type="text"
                        name="website"
                        id="Website"
                        value={user && user.website ? user.website : website}
                        onChange={(e) => setWebsite(e.target.value)}
                    />
                </div>
                <div className="bio__input">
                    <label htmlFor="Bio">Bio</label>
                    <input
                        type="text"
                        name="bio"
                        id="Bio"
                        value={user && user.bio ? user.bio : website}
                        onChange={(e) => setBio(e.target.value)}
                    />
                </div>

                <button className="submit__btn" type="submit">
                    submit
                    </button>

            </ProfileForm>

        </Wrapper>
    )
}

export default EditProfilePage;
