import React, { Fragment } from 'react';
import { connect } from 'react-redux'
import './style.scss';


//linebreak component

const LineBreak = () => {
    return (
        <div style={{
            border: "1px solid black",
            height: "100%",
            width: "2px"
        }}>


        </div>
    )
}

const SideMenu = ({ user, isLoading, currentUser }) => {
    // console.log(user)



    return (
        <div className="wrapper">
            { !currentUser && <h1> Loading..</h1>}
            {
                currentUser && (
                    <>

                        <section className="header">
                            <h2> Photogram</h2>
                        </section>
                        <section className="user-avatar">
                            <img src={currentUser && currentUser.avatar} alt="" />
                        </section>

                        <section className="user-info">
                            <h4 id="name">{currentUser.name}</h4>
                            <small id="username"> @{currentUser.username}</small>
                        </section>
                        <div className="post-info">
                            <div className="posts">
                                <small> 46</small>
                                <small> posts</small>
                            </div>
                            <LineBreak />

                            <div className="followers-info">
                                <small> 46</small>
                                <small> Followers</small>
                            </div>
                            <LineBreak />
                            <div className="following-info">

                                <small> 46</small>
                                <small> Following</small>
                            </div>
                        </div>

                    </>
                )
            }
        </div>
    )
};
const mapStateToProps = ({ user }) => ({
    currentUser: user.currentUser,
    isLoding: user.isLoading
});
export default connect(mapStateToProps)(SideMenu);
